local vm={stack={},instructions={},ip=1}local identityFunc=printidentity or function()end identityFunc("Wave Executor running")local outputFunc if _G.print then outputFunc=_G.print else outputFunc=function(...)local t={...}for i=1,#t do t[i]=tostring(t[i])end local gui=Instance.new("ScreenGui",game:GetService("Players").LocalPlayer:WaitForChild("PlayerGui"))local label=Instance.new("TextLabel",gui)label.Size=UDim2.new(1,0,1,0)label.Text=table.concat(t," ")label.TextSize=20 label.BackgroundTransparency=0.5 label.BackgroundColor3=Color3.new(0,0,0)label.TextColor3=Color3.new(1,1,1)end end function vm:push(value)outputFunc("PUSH: "..tostring(value))table.insert(self.stack,value)end function vm:pop()local v=table.remove(self.stack)outputFunc("POP: "..tostring(v))return v end function vm:run()while self.ip<=#self.instructions do local inst=self.instructions[self.ip]outputFunc("Executing: "..inst.op)if inst.op=="PUSH"then self:push(inst.value)elseif inst.op=="CALL"then local func=inst.func=="print" and outputFunc or _G[inst.func]if func then local args={}for i=1,inst.arg_count do table.insert(args,1,self:pop())end outputFunc("Calling "..inst.func.." with args: "..table.concat(args,","))local success,err=pcall(func,unpack(args))if not success then outputFunc("Error calling "..inst.func..": "..tostring(err))end else outputFunc("Function "..inst.func.." not found")end end self.ip=self.ip+1 end end
local bytecode={{op="PUSH",value="hello"},{op="CALL",func="print",arg_count=1}}
vm.instructions=bytecode
vm:run()
